def solution1():
    treeCount = 0

    for row in range(0, len(data)-1):
        col = (row+1) * 3
        while col >= len(data[row]):
            col -= len(data[row])

        if data[row+1][col] == '#':
            treeCount += 1

    return treeCount


def solution2(xOff_, yOff_):
    treeCount = 0
    x = xOff_
    y = yOff_

    while y < len(data):
        while x >= len(data[y]):
            x -= len(data[y])

        if data[y][x] == '#':
            treeCount += 1

        x += xOff_
        y += yOff_

    return treeCount

    
print("Year 2020, Day 3 solution part 1:", solution1())
print("Year 2020, Day 3 solution part 2:", (solution2(1, 1) * solution2(3, 1) * solution2(5, 1) * solution2(7, 1) * solution2(1, 2)))
