def solution1():
    # var to track the total number of points from illegal characters
    points = 0

    # Looping through each line in the data
    for line in data:
        # As long as a pair has been found, we keep looping
        while True:
            pairFound = False
            
            if "()" in line:
                line = line.replace("()", "", 1)
                pairFound = True
            if "[]" in line:
                line = line.replace("[]", "", 1)
                pairFound = True
            if "{}" in line:
                line = line.replace("{}", "", 1)
                pairFound = True
            if "<>" in line:
                line = line.replace("<>", "", 1)
                pairFound = True

            # If no pairs were found in this loop, we break out
            if not pairFound:
                break

        # Looping through the remaining chars in the line until we hit an end
        for char in line:
            if char == ')':
                points += 3
                break
            elif char == ']':
                points += 57
                break
            elif char == '}':
                points += 1197
                break
            elif char == '>':
                points += 25137
                break

    print("Year 2021, Day 10 solution part 1:", points)


def solution2():
    # array to track the points for each line in the data
    points = []

    # Looping through each line in the data
    for line in data:
        # As long as a pair has been found, we keep looping
        while True:
            pairFound = False
            
            if "()" in line:
                line = line.replace("()", "", 1)
                pairFound = True
            if "[]" in line:
                line = line.replace("[]", "", 1)
                pairFound = True
            if "{}" in line:
                line = line.replace("{}", "", 1)
                pairFound = True
            if "<>" in line:
                line = line.replace("<>", "", 1)
                pairFound = True

            # If no pairs were found in this loop, we break out
            if not pairFound:
                break

        # If we find an ending character remaining, then the line is corrupted and ignored
        corrupt = False
        for char in line:
            if char == ')' or char == ']' or char == '}' or char == '>':
                corrupt = True
                break

        # If the line isn't corrupt, we can find the point value of the closing chars
        if not corrupt:
            linePoints = 0
            # Iterating backwards
            for i in range(len(line)-1, -1, -1):
                linePoints *= 5
                if line[i] == '(':
                    linePoints += 1
                elif line[i] == '[':
                    linePoints += 2
                elif line[i] == '{':
                    linePoints += 3
                elif line[i] == '<':
                    linePoints += 4

            points.append(linePoints)

    # Sorting the point values in order from low to high
    for i in range(0, len(points) - 1):
        for j in range(i+1, len(points)):
            if points[i] > points[j]:
                placeholder = points[i]
                points[i] = points[j]
                points[j] = placeholder

    # Returning the median point value
    print("Year 2021, Day 10 solution part 2:", points[(len(points) - 1) // 2])


solution1()
solution2()