def solution1():
    dc = data.copy()

    iteration = 0

    while True:
    #for i in range(0, 1):
        iteration += 1

        # List of indexes to shift right
        rightShift = []

        # Finding all of the > arrows that can shift right
        for r in range(0, len(dc)):
            for c in range(0, len(dc[r])):
                if dc[r][c] == '>':
                    if c < len(dc[r]) - 1:
                        if dc[r][c+1] == '.':
                            rightShift.append((r,c))
                    elif dc[r][0] == '.':
                        rightShift.append((r,c))

        # Shifting all of the > arrows at the same time
        for rs in rightShift:
            if rs[1] < len(dc[0]) - 1:
                dc[rs[0]][rs[1]] = '.'
                dc[rs[0]][rs[1]+1] = '>'
            else:
                dc[rs[0]][rs[1]] = '.'
                dc[rs[0]][0] = '>'

        # List of indexes to shift down
        downShift = []

        # Finding all of the v arrows that can shift down
        for r in range(0, len(dc)):
            for c in range(0, len(dc[r])):
                if dc[r][c] == 'v':
                    if r < len(dc) - 1:
                        if dc[r+1][c] == '.':
                            downShift.append((r,c))
                    elif dc[0][c] == '.':
                        downShift.append((r,c))

        # Shifting all of the v arrows down at the same time
        for ds in downShift:
            if ds[0] < len(dc) - 1:
                dc[ds[0]][ds[1]] = '.'
                dc[ds[0]+1][ds[1]] = 'v'
            else:
                dc[ds[0]][ds[1]] = '.'
                dc[0][ds[1]] = 'v'

        # If there were no arrow shifts this iteration, everything has come to a stop
        if len(rightShift) == 0 and len(downShift) == 0:
            return iteration

        
print("Year 2021, Day 25 solution part 1:", solution1())