def basinBFS(r_, c_):
    # Queue for positions to check
    q = [(r_, c_)]
    # Dictionary for positions that have already been checked
    checked = {}

    while len(q) > 0:
        curr = q.pop(0)
        row = curr[0]
        col = curr[1]

        # Checking up
        if row > 0:
            if data[row-1][col] != 9 and (row-1, col) not in checked.keys():
                q.append((row-1, col))
        # Checking down
        if row < len(data) - 1:
            if data[row+1][col] != 9 and (row+1, col) not in checked.keys():
                q.append((row+1, col))
        # Checking left
        if col > 0:
            if data[row][col-1] != 9 and (row, col-1) not in checked.keys():
                q.append((row, col-1))
        # Checking right
        if col < len(data[0]) - 1:
            if data[row][col+1] != 9 and (row, col+1) not in checked.keys():
                q.append((row, col+1))

        # Putting this position in our dictionary of checked positions
        checked[curr] = 1

    return len(checked.keys())


def solution1():
    # var to hold the risk value of each low-point found
    risk = 0

    for row in range(0, len(data)):
        for col in range(0, len(data[row])):
            isLow = True

            # Checking up
            if row > 0:
                if data[row][col] >= data[row-1][col]:
                    isLow = False
            # Checking down
            if row < len(data) - 1:
                if data[row][col] >= data[row+1][col]:
                    isLow = False
            # checking left
            if col > 0:
                if data[row][col] >= data[row][col-1]:
                    isLow = False
            # checking right
            if col < len(data[row]) - 1:
                if data[row][col] >= data[row][col+1]:
                    isLow = False

            if isLow:
                risk += 1 + data[row][col]

    print("Year 2021, Day 9 solution part 1:", risk)


def solution2():
    # vars to hold the sizes of the 3 larges basins
    basin1 = 0
    basin2 = 0
    basin3 = 0

    for row in range(0, len(data)):
        for col in range(0, len(data[row])):
            isLow = True

            # Checking up
            if row > 0:
                if data[row][col] >= data[row-1][col]:
                    isLow = False
            # Checking down
            if row < len(data) - 1:
                if data[row][col] >= data[row+1][col]:
                    isLow = False
            # checking left
            if col > 0:
                if data[row][col] >= data[row][col-1]:
                    isLow = False
            # checking right
            if col < len(data[row]) - 1:
                if data[row][col] >= data[row][col+1]:
                    isLow = False

            # If it is a low-point, we do a BFS from this position
            if isLow:
                size = basinBFS(row, col)
                # If it's in the top-3 basin sizes we store it for later
                if size > basin1:
                    basin3 = basin2
                    basin2 = basin1
                    basin1 = size
                elif size > basin2:
                    basin3 = basin2
                    basin2 = size
                elif size > basin3:
                    basin3 = size

    print("Year 2021, Day 9 solution part 2:", (basin1 * basin2 * basin3))


